function CalculateSalesRateIncTax(n){var i=0,t=0,r=0,u=!1,f;$(".row-no-"+n+" td .SalesRate").val()!=""&&(i=parseFloat($(".row-no-"+n+" td .SalesRate").val()),t=i);$(".row-no-"+n+" td .TaxAmount").val()!=null&&(r=parseFloat($(".row-no-"+n+" td .TaxAmount").val()),u=$(".row-no-"+n+" td .TaxIsInPercent")[0].checked,u?(f=r*i/100,t=t+f):t=t+r);t!=null&&(t=Number(t.toFixed(2)));console.log("sales rate: ",i);console.log("Tax Amount: ",r);console.log("TaxIsInPercent: ",u);console.log("SalesRateFinal: ",t);$(".row-no-"+n+" td .SalesRateFinal").html(t)}$(document).ready(function(){for(var t=$("table#add-stock-table tbody tr").length,n=0;n<t;n++)CalculateSalesRateIncTax(n)});